mapper.select:
SELECT sa.id sid,sa.device_no sdevice_no,sa.access_date saccess_date,sa.attence_status sattence_status,sa.student_no sstudent_no, sa.absence_process_status sabsence_process_status,sa.process_person sprocess_person, sa.process_desc sprocess_desc,sa.create_time screate_time,sa.modify_time smodify_time,bs.student_name sstudent_name,bs.`class_name` className FROM sd_attence sa LEFT JOIN b_student bs ON sa.student_no = bs.student_no where 1=1
and DATE_FORMAT(access_date,'%Y-%m-%d') =DATE_FORMAT(#{date},'%Y-%m-%d')
order by sa.access_date desc limit #{currIndex},#{pageSize}

SELECT a.`id` id,s.`student_name` studentName,CONCAT(bu.dict_label,st.`dict_label`,d.`dormitory_no`) dorAddress
FROM sd_attence a LEFT JOIN b_student s ON a.`student_no` = s.`student_no` LEFT JOIN b_dormitory d ON s.`bdormitory_id` = d.`id` LEFT JOIN sys_dict_detail bu ON bu.`dict_type`='sys_building' AND bu.`dict_value`=s.`building_no` LEFT JOIN sys_dict_detail st ON st.`dict_type`='sys_storey' AND st.`dict_value`=s.`storey` WHERE a.`attence_status` = 2
AND s.`building_no` = #{building}
AND st.`dict_value` = #{storey}
AND s.`dormitory_no` =#{bdormitory}
AND DATE_FORMAT(a.`access_date`,'%Y-%m-%d') = DATE_FORMAT(#{checkDate},'%Y-%m-%d')

SELECT d.`id` id,( SELECT COUNT(1) FROM b_student bs WHERE bs.bdormitory_id=d.id)total ,CONCAT(bu.dict_label,st.`dict_label`,d.`dormitory_no`) roomName, COUNT( (SELECT sd.dict_label FROM sys_dict_detail sd WHERE sd.dict_type = 'attence_status' AND dict_value = attc.`attence_status` AND sd.dict_value=1) ) AS normal, COUNT( (SELECT sd.dict_label FROM sys_dict_detail sd WHERE sd.dict_type = 'attence_status' AND dict_value = attc.`attence_status` AND sd.dict_value=2) ) AS absence, COUNT( (SELECT sd.dict_label FROM sys_dict_detail sd WHERE sd.dict_type = 'attence_status' AND dict_value = attc.`attence_status` AND sd.dict_value=3) ) AS comebacklate, COUNT( (SELECT sd.dict_label FROM sys_dict_detail sd WHERE sd.dict_type = 'attence_status' AND dict_value = attc.`attence_status` AND sd.dict_value=4) ) AS leaveCount, GROUP_CONCAT((SELECT sd.dict_label FROM sys_dict_detail sd WHERE sd.dict_type='attence_status' AND dict_value=attc.`attence_status`)) AS attenceStatus FROM b_dormitory d LEFT JOIN sys_dict_detail bu ON bu.`dict_type`='sys_building' LEFT JOIN sys_dict_detail st ON st.`dict_type`='sys_storey' LEFT JOIN b_student s ON s.`bdormitory_id` = d.`id` AND s.`building_no` = bu.`dict_value` AND s.`storey` = st.`dict_value` LEFT JOIN sd_attence attc ON attc.`student_no` = s.`student_no` WHERE 1=1
AND s.building_no = #{building}
AND st.`dict_value` = #{storey}
AND d.`dormitory_no` = #{bdormitory}
AND DATE_FORMAT(attc.`access_date`,'%Y-%m-%d') = DATE_FORMAT( #{checkDate},'%Y-%m-%d')
GROUP BY d.`id`
